import { render } from '@testing-library/react';
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
// import App from './App';
import reportWebVitals from './reportWebVitals';

function Contents(props) {
  let createInnerHTML = (str) => {
    return { __html: str };
  }
  let ReplaceTags = (str) => {
    return str.replace(/<("[^"]*"|'[^']*'|[^'">])*>/g, '');
  }

  let cls = props.class+"";
  console.log(cls)
  let body = ~cls.indexOf("articleList") ?
    createInnerHTML(props.text + "<hr/>") :
    createInnerHTML("<img src='" + props.img + "'>" + ReplaceTags(props.text) + "<hr/>");

  return (
    <div className={"content " + props.class} id={props.id}>
      <h2 className="entry_header">{props.title}</h2>
      <div className="entry_body"
        dangerouslySetInnerHTML={body}>
      </div>
      <div className="entry_footer">
        <div className="pagetop">
          <p>{props.date}</p>
          <a href="#header" title="ページのトップへ">ページのトップへ</a>
        </div>
      </div>
    </div>);
}

class Header extends React.Component {
  render() {
    return (
      <a href="?#">
        <div id="header">
          <h1 className="noDecoration">pon&nbsp;suke人生リークBlog</h1>
        </div>
      </a>
    );
  }
}

class MainContents extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      loading: false
    }
  }

  componentDidMount() {
    //URLパラメータを文字列で取得(?含む)
    let urlParamStr = window.location.search
    if (urlParamStr) {
      //?を除去
      urlParamStr = urlParamStr.substring(1)
      let params = {}
      //urlパラメータをオブジェクトにまとめる
      urlParamStr.split('&').forEach(param => {
        const temp = param.split('=')
        //pramsオブジェクトにパラメータを追加
        params = {
          ...params,
          [temp[0]]: temp[1]
        }
      })
      let tmp = this.state;
      this.setState({
        ...tmp,
        params
      })
    }

    // 記事を取得
    const key = {
      headers: { 'X-API-KEY': '9b30e206-4b28-4453-91f5-5d39d40d15a3' },
    };
    return fetch('https://ponsuke.microcms.io/api/v1/diary', key)
      .then((response) => response.json())
      .then((responseJson) => {
        let tmp = this.state;
        this.setState({
          ...tmp,
          loading: true,
          data: responseJson.contents,
        });
        console.log(responseJson)
      })
      .catch((error) => {
        console.error(error);
      });
  }
  renderContents(i, c = "") {
    return <Contents
      key={i.id}
      title={i.title}
      text={i.main}
      date={i.updatedAt}
      class={c + "maincontent"}
      img={i.thumbnail.url}
    />;
  }

  render() {
    if (this.state.loading) {
      let p = this.state.params.p;
      let hoge = "";
      if (Math.sign(p)) hoge = "articleList";
      let pageNumber = Math.sign(p) == 1 ? p : -1;
      console.log(pageNumber);

      return (
        <div id="main" >
          {this.state.data.map(d => {
            return this.renderContents(d, hoge)
          })}
        </div>
      );
    } else {
      return (
        <div>
          <p>Loading...</p>
        </div>
      );
    }
  }
}

class SideContents extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      loading: false
    }
  }
  componentDidMount() {
    const key = {
      headers: { 'X-API-KEY': '9b30e206-4b28-4453-91f5-5d39d40d15a3' },
    };
    return fetch('https://ponsuke.microcms.io/api/v1/side', key)
      .then((response) => response.json())
      .then((responseJson) => {
        this.setState({
          loading: true,
          data: responseJson.contents,
        });
      })
      .catch((error) => {
        console.error(error);
      });
  }
  renderContents(d) {
    console.log(d);
    return <Contents
      key={d.id}
      title={d.title}
      text={d.content}
      id="profile"
    />;
  }
  render() {
    if (this.state.loading) {
      return (
        <div id="side" >
          {this.state.data.map(d => {
            return this.renderContents(d)
          })}
        </div>
      );
    } else {
      return (
        <div>
          <p></p>
        </div>
      );
    }
  }
}

class Container extends React.Component {
  render() {
    return (
      <div id="container">
        <MainContents />
        <SideContents />
      </div>
    );
  }
}

class Wrap extends React.Component {
  render() {
    return (
      <div id="wrapper">
        <Header />
        <Container />
      </div>
    )
  }

}

ReactDOM.render(
  <Wrap />,
  document.getElementById('root')
);


// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
