import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
// import App from './App';
import reportWebVitals from './reportWebVitals';

function Contents(props) {
  return (
    <div class="content">
      <h2 class="entry_header">{props.title}</h2>
      <div class="entry_body">{props.text}</div>
      <div class="entry_footer">
        <div class="pagetop">
          <p>{props.date}</p>
          <a href="#header" title="ページのトップへ">ページのトップへ</a>
        </div>
      </div>
    </div>);
}

class Header extends React.Component {
  render() {
    return (
      <a href="?#">
        <div id="header">
          <h1 class="noDecoration">pon&nbsp;suke人生リークBlog</h1>
        </div>
      </a>
    );
  }
}

class MainContents extends React.Component {
  renderContents(i) {
    return <Contents
      title={"title" + i}
      text={"text"}
      date={"today"}
    />;
  }

  render() {
    let list = [];
    let data = [
      { text: "1" },
      { text: "2" }
    ];
    for (var i in data) {
      list.push(<li>{data[i].text}</li>);
    }

    return (
      <div id="main" >
        {this.renderContents(1)}
        {this.renderContents(2)}
        {this.renderContents(3)}
      </div>
    );
  }
}

class Container extends React.Component {
  render() {
    return (
      <div id="container">
        <MainContents />
      </div>
    );
  }
}

class Wrap extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      loading: false
    }
  }

  componentDidMount() {
    const key = {
      headers: { 'X-API-KEY': "b30e206-4b28-4453-91f5-5d39d40d15a3"},
    };
    return fetch('https://ponsuke.microcms.io/api/v1/diary')
      .then((response) => response.json())
      .then((responseJson) => {
        this.setState({
          loading: true,
          data: responseJson,
        });

      })
      .catch((error) => {
        console.error(error);
      });
  }

  render() {
    return (
      <div id="wrapper">
        <Header />
        <Container />
      </div>
    )
  }

}

ReactDOM.render(
  <Wrap />,
  document.getElementById('root')
);


// If you want to start measuring performance in your app, pass a function
